package controlStatment;

public class Switch_ {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a=20,b=10;
		switch(2)
		{
		 case 1:System.out.println("sum of "+(a+b));
		break;
		case 2:System.out.println("sub of "+(a-b));
		break;
		case 3:System.out.println("Mul of "+(a*b));
		break;
		case 4:System.out.println("Div of "+(a/b));
		break;
		default : System.out.println("Invalid data");
		}
	}

}
