package data_type_1;

public class PrimitiveDataType
{

	 public static void main(String[] args) 
	 {
		// TODO Auto-generated method stub
		byte a;
		a=7;
		System.out.println("my favarite number is "+a);
		short b;
		b=12822;
		System.out.println("my income is "+b);
		int c;
		c=140995;
		System.out.println("my date of birth is "+c);
		long d;
		d=9503887981l;
		System.out.println("My Mobile NUmber is "+d);
		float e;
		e=86.91f;
		System.out.println("My 10th Percentage is "+ e);
		double f;
		f=91.46122002;
		System.out.println("My another Number is "+f);
		char grade;
		grade='G';
		System.out.println("juneer "+grade);
			
		boolean result;
		result=true;
		System.out.println("my result is "+ result);
		
		String h;// non primitive data type
		h="Velocity";
		System.out.println("My String is "+h);
	
	 }
  }


